/* Top box (content layer) */
.sidebar-top-box {
    position: absolute;
    left: 16px;
    top: 57px;
    width: 419px;
    height: 848px;
    background-color: #ffffff;
    border-radius: 24px;
    overflow-y: auto;
    overflow-x: hidden;
    padding: 40px 32px;
    box-sizing: border-box;
}

/* Variable heading - positioned above both boxes */
.sidebar-variable-heading {
    position: absolute;
    left: 40px; /* 1220px - 1180px container position */
    top: -28px; /* Lowered 18px: -46px + 18px */
    width: 374px;
    height: 39px;
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-style: normal;
    font-size: 32pt;
    color: #ffffff;
    text-transform: lowercase;
    line-height: 1;
    z-index: 1000; /* High z-index to ensure visibility */
}

/* FCRA stays uppercase */
.sidebar-variable-heading .uppercase {
    text-transform: uppercase;
}

/* Old sidebar styles - to be removed after HTML update */
.violation-sidebar, .tagger-sidebar, .evidence-sidebar {
    display: none;
}

.sidebar-heading {
    font-size: 24px;
    font-weight: var(--font-bold);
    margin: 0 0 24px 0;
}

/* Violation Statistics */
.violation-stats {
    display: grid;
    gap: 16px;
    margin-bottom: 32px;
}

.stat-card {
    display: flex;
    align-items: center;
    gap: 16px;
    padding: 16px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    border: 1px solid var(--border-subtle);
    box-shadow: var(--shadow-sm);
}

.stat-icon {
    width: 32px;
    height: 32px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.stat-icon i {
    width: 20px;
    height: 20px;
    color: #ffffff;
}

.stat-details {
    display: flex;
    flex-direction: column;
}

.stat-label {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    color: var(--text-secondary);
    letter-spacing: 0;
}

.stat-value {
    font-size: 32px;
    font-weight: var(--font-bold);
    color: var(--text-primary);
}

/* Violation List */
.violation-list {
    margin-top: 32px;
}

.list-heading {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    margin: 0 0 16px 0;
}

.violations {
    list-style: none;
    padding: 0;
    margin: 0;
}

.violation-item {
    display: flex;
    align-items: flex-start;
    gap: 12px;
    padding: 12px;
    margin-bottom: 8px;
    background-color: var(--bg-accent);
    border-radius: 24px;
    transition: all var(--transition-fast);
    border: 1px solid var(--border-subtle);
}

.violation-item:hover {
    background-color: var(--bg-hover);
    transform: translateX(4px);
    box-shadow: var(--shadow-sm);
}

.violation-item.selected {
    background-color: rgba(0, 102, 255, 0.1);
    border-color: var(--color-primary);
}

.violation-item-icon {
    flex-shrink: 0;
}

.violation-item-text {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.violation-severity {
    font-size: 14px;
    font-weight: var(--font-medium);
    text-transform: uppercase;
    letter-spacing: 0.5px;
    color: var(--text-secondary);
}

.violation-description {
    font-size: 16px;
    font-weight: var(--font-regular);
    color: var(--text-primary);
    line-height: 1.4;
}

/* ========================================
   Violation Filter Styles
   ======================================== */
.violation-filter {
    margin-bottom: 24px;
    padding: 16px;
    background-color: var(--bg-accent);
    border-radius: 12px;
    border: 1px solid var(--border-subtle);
}

.filter-heading {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    margin: 0 0 12px 0;
    color: var(--text-secondary);
}

.filter-options {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.filter-option {
    display: flex;
    align-items: center;
    gap: 8px;
    cursor: pointer;
    font-family: 'Space Grotesk', sans-serif;
    font-size: 14px;
    font-weight: 500;
    color: var(--text-primary);
}

.filter-option input[type="radio"] {
    width: 16px;
    height: 16px;
    margin: 0;
    cursor: pointer;
    accent-color: var(--color-primary);
}

.filter-option span {
    text-transform: lowercase;
}

/* ========================================
   Evidence Sidebar Styles
   ======================================== */
.evidence-summary {
    background-color: var(--bg-accent);
    border-radius: 12px;
    padding: 20px;
    margin-bottom: 24px;
    border: 1px solid var(--border-subtle);
}

.summary-item {
    display: flex;
    justify-content: space-between;
    align-items: baseline;
    margin-bottom: 12px;
}

.summary-item:last-child {
    margin-bottom: 0;
}

.summary-label {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 14px;
    font-weight: 700;
    color: var(--text-secondary);
    text-transform: lowercase;
}

.summary-value {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 14px;
    font-weight: 700;
    color: var(--text-primary);
    text-transform: lowercase;
}

.evidence-metadata {
    padding: 20px;
    background-color: var(--bg-accent);
    border-radius: 12px;
    border: 1px solid var(--border-subtle);
}

/* ========================================
   Responsive Design
   ======================================== */
@media (max-width: 1024px) {
    .docket-content {
        gap: 16px;
    }

    .nav-left, .nav-center, .nav-right {
        gap: 16px;
    }

    .violation-sidebar {
        width: 320px;
    }
}

@media (max-width: 768px) {
    .content-wrapper {
        flex-direction: column;
    }

    .violation-sidebar, .tagger-sidebar, .evidence-sidebar {
        width: 100%;
        border-left: none;
        border-top: 1px solid var(--border-subtle);
        margin-top: 16px;
        height: auto;
    }

    .viobox {
        width: 90%;
        max-width: 557px;
    }
}

