    padding: 24px 24px 0 24px;
}

.filter-report-title {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-size: 30pt;
    color: #253541;
    text-transform: lowercase;
    line-height: 1;
    margin: 0;
}

.filter-report-toggle {
    width: 28px;
    height: 28px;
    background: transparent;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    transition: background-color 200ms ease;
    padding: 0;
}

.filter-report-toggle:hover {
    background-color: rgba(37, 53, 65, 0.05);
}

.filter-report-toggle svg {
    width: 28px;
    height: 28px;
    stroke: #64748B;
    transition: transform 200ms ease;
}

.filter-this-report.collapsed .filter-report-toggle svg {
    transform: rotate(180deg);
}

.filter-report-content {
    padding: 16px 24px 24px 24px;
    transition: all 300ms ease;
    transform-origin: top;
}

.filter-this-report.collapsed .filter-report-content {
    height: 0;
    padding-top: 0;
    padding-bottom: 0;
    opacity: 0;
    overflow: hidden;
}

.filter-report-intro {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 11pt;
    color: rgba(37, 53, 65, 0.9);
    line-height: 1.4;
    margin: 0 0 20px 0;
    max-width: 381px;
}

.filter-report-options {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 16px 30px;
    width: 390px;
    background: #F3F4F6;
    padding: 20px 30px;
    border-radius: 16px;
    margin-left: -6px; /* Adjust to align with design */
}

.filter-report-option {
    display: flex;
    align-items: center;
    gap: 8px;
    cursor: pointer;
    position: relative;
}

.filter-report-option input[type="radio"] {
    width: 18px;
    height: 18px;
    margin: 0;
    cursor: pointer;
    accent-color: #0066FF;
    position: relative;
    flex-shrink: 0;
}

.filter-report-option span {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 400;
    font-size: 14px;
    color: rgba(37, 53, 65, 0.9);
    line-height: 1.2;
    user-select: none;
}

