439	        box.addEventListener('click', (e) => {
440	            e.preventDefault();
441	            this.selectViolation(violation, e.currentTarget);
442	        });
443	        
444	        return box;
445	    }
446	    
447	    selectViolation(violation, viobox) {
448	        const isSelected = this.selectedViolations.has(violation.id);
449	        
450	        if (isSelected) {
451	            // Deselect
452	            this.selectedViolations.delete(violation.id);
453	            viobox.classList.remove('active');
454	            this.announce(`Deselected ${violation.severity} severity violation`);
455	        } else {
456	            // Select
457	            this.selectedViolations.add(violation.id);
458	            viobox.classList.add('active');
459	            this.announce(`Selected ${violation.severity} severity violation: ${violation.text}`);
460	        }
461	        
462	        // Update counter and sidebar
463	        this.updateViolationCounter();
464	        this.updateViolationSidebar();
465	    }
466	    
467	    updateViolationSidebar() {
468	        // Get the selected filter
469	        const selectedFilter = document.querySelector('input[name="violation-filter"]:checked')?.value || 'total';
470	        
471	        let violationsToCount = [];
472	        let stats = { high: 0, medium: 0, low: 0 };
473	        
474	        switch (selectedFilter) {
475	            case 'total':
476	                // Get ALL violations for current creditor/bureau/date (across all pages)
477	                violationsToCount = this.getAllViolationsForCurrentReport();
478	                break;
479	                
480	            case 'selected':
481	                // Get only selected violations across all pages
482	                violationsToCount = this.getSelectedViolationsForCurrentReport();
483	                break;
484	                
485	            case 'page':
486	                // Get only violations on current page
487	                violationsToCount = this.violations;
488	                break;
489	        }
490	        
491	        // Count violations by severity
