    pointer-events: none;
    opacity: 0;
    transition: opacity var(--transition-fast);
    z-index: 10000;
    box-shadow: var(--shadow-lg);
}

.viobox-tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: #f26419;
}

.tooltip-mode .viobox:hover .viobox-tooltip {
    opacity: 1;
}

/* ========================================
   Enhanced Sidebar Top Stack Components
   ======================================== */

/* Main wrapper for the stacked components */
.sidebar-top-stack {
    display: flex;
    flex-direction: column;
    gap: 24px;
    width: 100%;
}

/* ========================================
   Filter This Report Component
   ======================================== */
.filter-this-report {
    background: #FFFFFF;
    border-radius: 24px;
    border: 1px solid #E2E8F0;
    overflow: hidden;
    transition: all 300ms ease;
}

.filter-report-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 24px 24px 0 24px;
}

.filter-report-title {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-size: 30pt;
    color: #253541;
    text-transform: lowercase;
    line-height: 1;
    margin: 0;
}

.filter-report-toggle {
    width: 28px;
    height: 28px;
    background: transparent;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    transition: background-color 200ms ease;
    padding: 0;
}

.filter-report-toggle:hover {
    background-color: rgba(37, 53, 65, 0.05);
}

.filter-report-toggle svg {
    width: 28px;
    height: 28px;
    stroke: #64748B;
    transition: transform 200ms ease;
}

.filter-this-report.collapsed .filter-report-toggle svg {
    transform: rotate(180deg);
}

.filter-report-content {
    padding: 16px 24px 24px 24px;
    transition: all 300ms ease;
    transform-origin: top;
}

.filter-this-report.collapsed .filter-report-content {
    height: 0;
    padding-top: 0;
    padding-bottom: 0;
    opacity: 0;
    overflow: hidden;
}

.filter-report-intro {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 11pt;
    color: rgba(37, 53, 65, 0.9);
    line-height: 1.4;
    margin: 0 0 20px 0;
    max-width: 381px;
}

.filter-report-options {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 16px 30px;
    width: 390px;
    background: #F3F4F6;
    padding: 20px 30px;
    border-radius: 16px;
    margin-left: -6px; /* Adjust to align with design */
}

.filter-report-option {
    display: flex;
    align-items: center;
    gap: 8px;
    cursor: pointer;
    position: relative;
}

.filter-report-option input[type="radio"] {
    width: 18px;
    height: 18px;
    margin: 0;
    cursor: pointer;
    accent-color: #0066FF;
    position: relative;
    flex-shrink: 0;
}

.filter-report-option span {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 400;
    font-size: 14px;
    color: rgba(37, 53, 65, 0.9);
    line-height: 1.2;
    user-select: none;
}

/* Disabled state for mutually exclusive options */
.filter-report-option.disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.filter-report-option.disabled input[type="radio"] {
    cursor: not-allowed;
}

.filter-report-option.disabled span {
    color: rgba(37, 53, 65, 0.5);
}

/* Tooltip for disabled options */
.filter-report-option.disabled::after {
    content: attr(data-tooltip);
    position: absolute;
    bottom: 100%;
    left: 50%;
    transform: translateX(-50%);
    background: #253541;
    color: white;
    font-size: 12px;
    padding: 6px 12px;
    border-radius: 6px;
    white-space: nowrap;
    opacity: 0;
    pointer-events: none;
    transition: opacity 200ms ease;
    margin-bottom: 8px;
}

.filter-report-option.disabled:hover::after {
    opacity: 1;
}

/* Special layout for "selected violations" - spans full width */
.filter-report-option:first-child {
    grid-column: 1 / -1;
}

