/* ========================================
   Filter This Report Component
   ======================================== */
.filter-this-report {
    display: flex;
    flex-direction: column;
    gap: 8px;
    transition: all 300ms ease;
}

/* Container 1: Header + Intro Text */
.filter-header-container {
    background: transparent;
    border-radius: 24px;
    padding: 16px 24px 0 24px;
}

.filter-header-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
}

.filter-report-title {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-size: 30pt;
    color: #253541;
    text-transform: lowercase;
    line-height: 1;
    margin: 0;
}

.filter-report-toggle {
    width: 28px;
    height: 28px;
    background: transparent;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    transition: all 200ms ease;
    padding: 0;
}

.filter-report-toggle svg {
    width: 28px;
    height: 28px;
    stroke: #A8AEB3;
    transition: all 200ms ease;
}

.filter-report-toggle:hover svg {
    stroke: #F26419;
}

.filter-this-report.collapsed .filter-report-toggle svg {
    transform: rotate(180deg);
}

.filter-report-intro {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 11pt;
    color: rgba(37, 53, 65, 0.9);
    line-height: 1.4;
    margin: 0;
    padding: 0 24px;
    max-width: 381px;
}

/* Container 2: Filter Option Group */
.filter-options-container {
    width: 390px;
    background: #F3F4F6;
    border-radius: 16px;
    padding: 20px 30px;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 16px 30px;
    transition: all 300ms ease;
    transform-origin: top;
}

.filter-this-report.collapsed .filter-options-container {
    height: 0;
    padding-top: 0;
    padding-bottom: 0;
    opacity: 0;
    overflow: hidden;
    margin-top: -8px;
}

.filter-this-report.collapsed .filter-report-intro {
    opacity: 0;
    height: 0;
    overflow: hidden;
    margin-bottom: -8px;
}

.filter-report-option {
    display: flex;
    align-items: center;
    gap: 8px;
    cursor: pointer;
    position: relative;
}

.filter-report-option input[type="radio"] {
    width: 18px;
    height: 18px;
    margin: 0;
    cursor: pointer;
    accent-color: #0066FF;
    position: relative;
    flex-shrink: 0;
}

.filter-report-option span {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 400;
    font-size: 14px;
    color: rgba(37, 53, 65, 0.9);
    line-height: 1.2;
    user-select: none;
}

/* Disabled state for mutually exclusive options */
.filter-report-option.disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.filter-report-option.disabled input[type="radio"] {
    cursor: not-allowed;
}

.filter-report-option.disabled span {
    color: rgba(37, 53, 65, 0.5);
}

/* Tooltip for disabled options */
.filter-report-option.disabled::after {
    content: attr(data-tooltip);
    position: absolute;
    bottom: 100%;
    left: 50%;
    transform: translateX(-50%);
    background: #253541;
    color: white;
    font-size: 12px;
    padding: 6px 12px;
    border-radius: 6px;
    white-space: nowrap;
    opacity: 0;
    pointer-events: none;
    transition: opacity 200ms ease;
    margin-bottom: 8px;
}

.filter-report-option.disabled:hover::after {
    opacity: 1;
}

/* Special layout for "selected violations" - spans full width */
.filter-report-option:first-child {
    grid-column: 1 / -1;
}

