    border-radius: 6px;
    white-space: nowrap;
    opacity: 0;
    pointer-events: none;
    transition: opacity 200ms ease;
    margin-bottom: 8px;
    z-index: 10;
}

.filter-option.disabled:hover::after {
    opacity: 1;
}

