    color: white;
    font-size: 12px;
    padding: 6px 12px;
    border-radius: 6px;
    white-space: nowrap;
    opacity: 0;
    pointer-events: none;
    transition: opacity 200ms ease;
    margin-bottom: 8px;
}

.filter-report-option.disabled:hover::after {
    opacity: 1;
}

/* Special layout for "selected violations" - spans full width */
.filter-report-option:first-child {
    grid-column: 1 / -1;
}

