    color: #253541;
}

.canvas-placeholder p {
    font-size: 18px;
    font-weight: 400;
    color: #9ba1a6;
}

/* Canvas and sidebar are now fixed position, no flex needed */

.report-image {
    width: 100%;
    height: auto;
    max-height: 100%;
    object-fit: contain;
}

/* VIObox Container */
.viobox-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 3;
}

/* VIObox Styles */
.viobox {
    position: absolute;
    border: 2px solid rgba(203, 213, 225, 0.9); /* viobox-inactive with 0.9 opacity */
    border-radius: 13px;
    background-color: rgba(255, 255, 255, 0.8);
    display: flex;
    align-items: center;
    padding-left: 8px; /* Move content 8px from left */
    pointer-events: auto;
    cursor: pointer;
