/* Header container - always visible */
.filter-counter-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 16px 24px 0 24px;
    margin: 0 -24px; /* Negative margin to extend to container edges */
    position: relative;
    z-index: 1; /* Lower than heading to prevent overlap */
}

.filter-counter-title {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-size: 30pt;
    color: #253541;
    text-transform: lowercase;
    line-height: 1;
    margin: 0;
}

.filter-counter-toggle {
    width: 28px;
    height: 28px;
    background: transparent;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    transition: all 200ms ease;
    padding: 0;
}

.filter-counter-toggle svg {
    width: 28px;
    height: 28px;
    stroke: #9BA1A6; /* 30% tint of #253541 */
    fill: none;
    transition: all 200ms ease;
}

.filter-counter-toggle:hover svg {
    stroke: #F26419;
}

