}

/* Creditor segment */
.creditor-segment {
    position: absolute;
    left: 375px;
    width: 150px;
}

.creditor-segment .nav-text-container {
    max-width: 150px;
    overflow: hidden;
}

/* Page segment */
.page-segment {
    position: absolute;
    left: 600px;
    width: 62px;
