    line-height: 1;
    margin: 0;
}

.filter-counter-toggle {
    width: 28px;
    height: 28px;
    background: transparent;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    transition: all 200ms ease;
    padding: 0;
}

.filter-counter-toggle svg {
    width: 28px;
    height: 28px;
