/* Navigation tooltips */
.nav-tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
    transform: translateX(-50%);
    background-color: #f26419;
    color: #ffffff;
    font-family: 'Space Grotesk', sans-serif;
