    align-self: center;
}

/* Arrow Button - Inside header */
.docket-toggle {
    position: absolute;
    left: 27px;
    top: 50%;
    transform: translateY(-50%);
    width: 28px;
    height: 28px;
    background: none;
    border: none;
    cursor: pointer;
    color: #9ba1a6;
    padding: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: color var(--transition-fast);
    z-index: 10;
}

.docket-toggle:hover {
    color: #f26419;
}

.docket-toggle svg {
    width: 28px;
    height: 28px;
}

/* Adjust main-nav and content when docket collapses */
.main-nav {
    margin-top: 70px;
    transition: margin-top var(--transition-normal);
}

.docket-header.collapsed ~ .main-nav {
    margin-top: 20px; /* Move up when docket is collapsed */
}

/* ========================================
   Tool Tips Toggle
   ======================================== */
.tool-tips-toggle {
    position: fixed;
    top: 135px;
    left: 279px;
    width: 54px;
    height: 27.5px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
    border-radius: 36px;
    box-sizing: border-box;
    overflow: visible;
    z-index: 100;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
}

/* Tips indicator (sliding button) */
.tips-indicator {
    position: absolute;
    width: 27.4px;
    height: 27.4px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
    border-radius: 36px;
    box-sizing: border-box;
    transition: all 0.15s ease; /* Faster transition */
    left: -2px; /* OFF position - moved 2px left */
    z-index: 1;
}

/* Tips text */
.tips-text {
    position: absolute;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 10px; /* 10pt as specified */
    text-transform: lowercase;
    color: #9ba1a6;
    z-index: 2;
    pointer-events: none;
    user-select: none;
    line-height: 1;
    width: 20px;
    height: 11px;
    left: 3px; /* OFF state: x282 - container x279 = 3px */
    top: 6px; /* y141 - container y135 = 6px */
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.15s ease; /* Match indicator transition timing */
}

/* ON state */
.tool-tips-toggle[aria-checked="true"] .tips-indicator {
    left: 26.5px; /* Move to right for ON position (305.5px - 279px) */
    background-color: #f26419;
    border-color: #f8b98c;
}

.tool-tips-toggle[aria-checked="true"] .tips-text {
    color: #ffffff;
    left: 31px; /* ON state: x310 - container x279 = 31px */
}

/* ========================================
