}

/* ========================================
   Accessibility Utilities
   ======================================== */
.skip-link {
    position: absolute;
    top: -100px;
    left: 20px;
    z-index: var(--z-modal);
    padding: 12px 24px;
    background-color: var(--color-primary);
    color: var(--text-white);
    text-decoration: none;
    border-radius: 4px;
    font-weight: 600;
}

.skip-link:focus {
    top: 20px;
}

/* Focus visible styles for keyboard navigation */
:focus-visible {
    outline: 3px solid var(--color-primary);
    outline-offset: 2px;
    border-radius: inherit;
}

/* ========================================
   Layout Structure
   ======================================== */
.vioverse-app {
    position: relative;
    min-width: 1920px;
    min-height: 100vh;
    margin: 0 auto;
    background-color: #4f5a63;
    overflow: visible;
    z-index: 0;
}

/* ========================================
   Docket Header
   ======================================== */
.docket-header {
    position: fixed;
    top: 9px;
    left: 11px;
    width: 1898px;
    height: 48px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
    border-radius: 36px;
    display: flex;
    align-items: center;
    padding: 0 24px 0 27px; /* Left padding just 27px for button */
    transition: all var(--transition-normal);
    z-index: var(--z-base);
    overflow: hidden;
    box-sizing: border-box;
}

/* Collapsed state - container collapses */
.docket-header.collapsed {
    height: 0;
    padding-top: 0;
    padding-bottom: 0;
    border-width: 0;
    margin-top: -48px; /* Move everything up */
}

/* Down arrow when collapsed - no background */
.arrow-collapsed {
    position: fixed;
    top: 18px; /* 9px + 9px to center in the 48px space */
    left: 38px; /* 11px + 27px to maintain same x position */
    width: 28px;
    height: 28px;
    background: none;
    border: none;
    cursor: pointer;
    color: #9ba1a6;
    padding: 0;
    display: none;
    align-items: center;
    justify-content: center;
    transition: color var(--transition-fast);
    z-index: calc(var(--z-base) + 1);
}

.arrow-collapsed.visible {
    display: flex;
}

.arrow-collapsed:hover {
    color: #f26419;
}

.arrow-collapsed svg {
    width: 28px;
    height: 28px;
}

.docket-content {
    display: flex;
    align-items: center;
    gap: 24px;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 14px;
    line-height: 1;
    margin-left: 50px; /* Shift content right by 50px from button */
}

.field-item {
    display: flex;
    align-items: baseline;
    white-space: nowrap;
}

.field-label {
    color: #9ba1a6;
    text-transform: lowercase;
}

.field-value {
    color: #ffffff;
    text-transform: lowercase;
}

.divider {
    width: 1px;
    height: 30px;
    background-color: #9ba1a6;
    flex-shrink: 0;
    align-self: center;
}

/* Arrow Button - Inside header */
.docket-toggle {
    position: absolute;
    left: 27px;
    top: 50%;
    transform: translateY(-50%);
    width: 28px;
    height: 28px;
    background: none;
    border: none;
    cursor: pointer;
    color: #9ba1a6;
    padding: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: color var(--transition-fast);
    z-index: 10;
}

.docket-toggle:hover {
    color: #f26419;
}

.docket-toggle svg {
    width: 28px;
    height: 28px;
}

/* Adjust main-nav and content when docket collapses */
.main-nav {
    margin-top: 70px;
    transition: margin-top var(--transition-normal);
}

.docket-header.collapsed ~ .main-nav {
    margin-top: 20px; /* Move up when docket is collapsed */
}

/* ========================================
   Tool Tips Toggle
   ======================================== */
.tool-tips-toggle {
    position: fixed;
    top: 135px;
    left: 279px;
    width: 54px;
    height: 27.5px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
    border-radius: 36px;
    box-sizing: border-box;
    overflow: visible;
    z-index: 100;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
}

/* Tips indicator (sliding button) */
.tips-indicator {
    position: absolute;
    width: 27.4px;
    height: 27.4px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
    border-radius: 36px;
    box-sizing: border-box;
    transition: all 0.15s ease; /* Faster transition */
    left: -2px; /* OFF position - moved 2px left */
    z-index: 1;
}

/* Tips text */
.tips-text {
    position: absolute;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 10px; /* 10pt as specified */
    text-transform: lowercase;
    color: #9ba1a6;
    z-index: 2;
    pointer-events: none;
    user-select: none;
    line-height: 1;
    width: 20px;
    height: 11px;
    left: 3px; /* OFF state: x282 - container x279 = 3px */
    top: 6px; /* y141 - container y135 = 6px */
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.15s ease; /* Match indicator transition timing */
}

/* ON state */
.tool-tips-toggle[aria-checked="true"] .tips-indicator {
    left: 26.5px; /* Move to right for ON position (305.5px - 279px) */
    background-color: #f26419;
    border-color: #f8b98c;
}

.tool-tips-toggle[aria-checked="true"] .tips-text {
    color: #ffffff;
    left: 31px; /* ON state: x310 - container x279 = 31px */
}

/* ========================================
   New View Toggle
   ======================================== */
.view-toggle-new {
    position: fixed;
    top: 76px;
    left: 70px;
    width: 270px;
    height: 40px;
    background-color: #253541;
    border: 3px solid #9ba1a6;
    border-radius: 36px;
    box-sizing: border-box;
    overflow: visible;
    z-index: 100; /* High z-index to ensure clickability */
}

/* View labels container */
.view-labels {
    display: flex;
    position: absolute;
    width: 100%;
    height: 100%;
    align-items: center;
    z-index: 2;
}

/* View label buttons */
.view-label {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 16px;
    text-transform: lowercase;
    cursor: pointer;
    transition: color 0.3s ease;
    user-select: none;
    background: none;
    border: none;
    padding: 0;
    position: absolute;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    pointer-events: auto; /* Ensure clickability */
