    right: 8px;
}

.document-segment .nav-arrows {
    right: 8px;
}

.evidence-page-segment .nav-arrows {
    left: 64px;
}

/* Updated arrow button styles */
.nav-arrow-small {
    width: 17px;
    height: 17px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: transparent;
    border: none;
    color: #9ba1a6;
    transition: all var(--transition-fast);
    cursor: pointer;
    padding: 0;
