/* Header container - always visible */
.filter-counter-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 16px 24px 0 24px; /* Added top padding back */
    margin: 0 -24px; /* Negative margin to extend to container edges */
    position: relative;
