}

.filter-counter-title {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-size: 26pt;
    color: #253541;
    text-transform: lowercase;
    line-height: 1;
    margin: 0;
}

.filter-counter-toggle {
    width: 28px;
    height: 28px;
    background: rgba(255, 255, 255, 0.8); /* More visible white background */
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    transition: all 200ms ease;
    padding: 0;
    position: relative;
    z-index: 9999; /* Bring to front without affecting layout */
}

.filter-counter-toggle:hover {
    background: rgba(242, 100, 25, 0.1); /* Orange tint on hover */
}

.filter-counter-toggle svg {
    width: 28px;
    height: 28px;
    stroke: #253541 !important;
    stroke-opacity: 0.4 !important;
    fill: none !important;
    transition: all 200ms ease;
    position: relative;
    z-index: 9999;
}

.filter-counter-toggle:hover svg {
    stroke: #F26419;
    stroke-opacity: 1; /* Full opacity on hover */
}

