    z-index: 1000; /* Highest z-index to ensure it's on top */
}

.filter-option {
    display: flex;
    align-items: flex-start;
    gap: 8px;
    cursor: pointer;
    position: relative;
    padding: 0; /* No padding between filter rows */
}

.filter-option input[type="radio"] {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    width: 13px;
    height: 13px;
    border: 2px solid #253541; /* 2pt stroke */
    border-radius: 50%;
    background-color: #ffffff; /* White default fill */
    margin: 2px 0 0 0; /* Small top margin to align with text baseline */
    cursor: pointer;
    flex-shrink: 0;
    transition: background-color 200ms ease;
}

.filter-option input[type="radio"]:checked {
    background-color: #ff0000; /* Red selected fill */
}

.filter-option span {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 400;
    font-size: 14px;
    color: rgba(37, 53, 65, 0.9); /* #253541 at 90% opacity */
    line-height: 1.2;
    user-select: none;
}

/* Special layout for "selected violations" - spans full width */
.filter-option:first-child {
    grid-column: 1 / -1;
}

/* Disabled state */
.filter-option.disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.filter-option.disabled input[type="radio"] {
    cursor: not-allowed;
    opacity: 0.5;
}

.filter-option.disabled input[type="radio"]:checked {
    background-color: rgba(255, 0, 0, 0.5);
}

/* Tooltip for disabled options */
.filter-option.disabled::after {
    content: attr(data-tooltip);
    position: absolute;
    bottom: 100%;
    left: 50%;
    transform: translateX(-50%);
    background: #253541;
    color: white;
    font-size: 12px;
    padding: 6px 12px;
    border-radius: 6px;
    white-space: nowrap;
    opacity: 0;
    pointer-events: none;
    transition: opacity 200ms ease;
    margin-bottom: 8px;
    z-index: 10;
}

.filter-option.disabled:hover::after {
    opacity: 1;
}

