        });

        // Example of data-driven counter updates
        function updateSeverityCounts(counts) {
            if (counts.high !== undefined) {
                document.querySelector('[data-severity="high"]').textContent = counts.high;
            }
            if (counts.medium !== undefined) {
                document.querySelector('[data-severity="medium"]').textContent = counts.medium;
            }
            if (counts.low !== undefined) {
                document.querySelector('[data-severity="low"]').textContent = counts.low;
            }
        }

        // Example usage:
        // updateSeverityCounts({ high: 8, medium: 12, low: 4 });
    </script>
