   290	        <!-- Content section (collapsible) -->
   291	        <div class="severity-box-content">
   292	            <div class="severity-stack">
   293	                <!-- High Severity Box -->
   294	                <div class="severity-box severity-high">
   295	                    <div class="severity-label">high<br>severity</div>
   296	                    <div class="dot-counter-group">
   297	                        <div class="badge-dot">
   298	                            <i data-lucide="shield-x" stroke-width="2"></i>
   299	                        </div>
   300	                        <div class="severity-counter" data-severity="high">8</div>
   301	                    </div>
   302	                </div>
   303	                
   304	                <!-- Medium Severity Box -->
   305	                <div class="severity-box severity-medium">
   306	                    <div class="severity-label">medium<br>severity</div>
   307	                    <div class="dot-counter-group">
   308	                        <div class="badge-dot">
   309	                            <i data-lucide="triangle-alert" stroke-width="2"></i>
   310	                        </div>
   311	                        <div class="severity-counter" data-severity="medium">12</div>
   312	                    </div>
   313	                </div>
   314	                
   315	                <!-- Low Severity Box with Toggle Overlay -->
   316	                <div class="severity-box severity-low">
   317	                    <div class="severity-label">low<br>severity</div>
   318	                    <div class="dot-counter-group">
   319	                        <div class="badge-dot">
   320	                            <i data-lucide="circle-alert" stroke-width="2"></i>
   321	                        </div>
   322	                        <div class="severity-counter" data-severity="low">4</div>
   323	                    </div>
   324	                    <!-- Toggle button positioned on top of Low Severity box -->
   325	                    <button class="severity-box-toggle-overlay" aria-expanded="true" aria-controls="severity-content">
   326	                        <i data-lucide="chevron-up"></i>
   327	                    </button>
   328	                </div>
   329	            </div>
   330	        </div>
