   333	    <script>
   334	        // Initialize Lucide icons with specific settings
   335	        lucide.createIcons({
   336	            attrs: {
   337	                'stroke': 'white',
   338	                'stroke-width': 2,
   339	                'width': 28,
   340	                'height': 28
   341	            }
   342	        });
   343	        
   344	        // Collapsible Stack Pattern JavaScript
   345	        const container = document.querySelector('.severity-box-container');
   346	        const toggle = document.querySelector('.severity-box-toggle-overlay');
   347	        const content = document.querySelector('.severity-box-content');
   348	        
   349	        toggle.addEventListener('click', function() {
   350	            const isExpanded = this.getAttribute('aria-expanded') === 'true';
   351	            
   352	            // Toggle collapsed class
   353	            container.classList.toggle('collapsed');
   354	            
   355	            // Update aria-expanded
   356	            this.setAttribute('aria-expanded', !isExpanded);
   357	            
   358	            // Switch icon
   359	            const icon = this.querySelector('i');
   360	            if (isExpanded) {
   361	                icon.setAttribute('data-lucide', 'chevron-down');
   362	            } else {
   363	                icon.setAttribute('data-lucide', 'chevron-up');
   364	            }
   365	            
   366	            // Re-render icons
   367	            lucide.createIcons();
   368	            
   369	            // Announce state change for accessibility
   370	            const announcement = isExpanded ? 'Severity summary collapsed' : 'Severity summary expanded';
   371	            const liveRegion = document.createElement('div');
   372	            liveRegion.setAttribute('aria-live', 'polite');
   373	            liveRegion.setAttribute('aria-atomic', 'true');
   374	            liveRegion.style.position = 'absolute';
   375	            liveRegion.style.left = '-9999px';
   376	            liveRegion.textContent = announcement;
   377	            document.body.appendChild(liveRegion);
   378	            setTimeout(() => liveRegion.remove(), 1000);
   379	        });
   380	        
   381	        // Example of data-driven counter updates
   382	        function updateSeverityCounts(counts) {
   383	            ['high', 'medium', 'low'].forEach(severity => {
   384	                if (counts[severity] !== undefined) {
   385	                    const counter = document.querySelector(`[data-severity="${severity}"]`);
   386	                    counter.textContent = counts[severity];
   387	                    // Apply three-digits class for numbers >= 100
   388	                    if (counts[severity] >= 100) {
   389	                        counter.classList.add('three-digits');
   390	                    } else {
   391	                        counter.classList.remove('three-digits');
   392	                    }
   393	                }
   394	            });
   395	        }
   396	        
   397	        // Apply three-digits class on initial load
   398	        document.querySelectorAll('.severity-counter').forEach(counter => {
   399	            const value = parseInt(counter.textContent);
   400	            if (value >= 100) {
   401	                counter.classList.add('three-digits');
   402	            }
   403	        });
   404	        
   405	        // Example usage:
   406	        // updateSeverityCounts({ high: 8, medium: 12, low: 4 });
   407	    </script>
