// Initialize Lucide icons
lucide.createIcons();

// Collapsible toggle functionality
const container = document.querySelector('.severity-container');
const toggle = document.querySelector('.severity-toggle');
const content = document.querySelector('.severity-content');

toggle.addEventListener('click', function() {
    const isExpanded = this.getAttribute('aria-expanded') === 'true';

    // Toggle collapsed class
    container.classList.toggle('collapsed');

    // Update aria-expanded
    this.setAttribute('aria-expanded', !isExpanded);

    // Switch icon
    const icon = this.querySelector('i');
    if (isExpanded) {
        icon.setAttribute('data-lucide', 'chevron-down');
    } else {
        icon.setAttribute('data-lucide', 'chevron-up');
    }

    // Re-render icons
    lucide.createIcons();

    // Announce state change for accessibility
    const announcement = isExpanded ? 'Severity summary collapsed' : 'Severity summary expanded';
    const liveRegion = document.createElement('div');
    liveRegion.setAttribute('aria-live', 'polite');
    liveRegion.setAttribute('aria-atomic', 'true');
    liveRegion.style.position = 'absolute';
    liveRegion.style.left = '-9999px';
    liveRegion.textContent = announcement;
    document.body.appendChild(liveRegion);
    setTimeout(() => liveRegion.remove(), 1000);
});

// Example of data-driven counter updates

