/* ========================================
   Severity Summary Component
   ======================================== */
.severity-summary-component {
    position: relative;
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-top: 16px;
}

.severity-summary-header {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-style: normal;
    font-size: 30pt;
    color: rgba(37, 53, 65, 0.9);
    margin-bottom: 7px;
    text-transform: lowercase;
    letter-spacing: -0.02em;
    width: 100%;
    text-align: left;
}

/* Toggle button aligned to the right */
.severity-summary-toggle {
    position: absolute;
    right: 40px;
    top: 0;
    width: 40px;
    height: 40px;
    background: none;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 10;
    transition: all 300ms ease;
}

.severity-summary-toggle svg {
    color: #A8AEB3;
    width: 32px;
    height: 32px;
    transition: all 300ms ease;
}

.severity-summary-toggle:hover svg {
    color: #F26419;
}

/* Collapsible content */
.severity-summary-content {
    overflow: hidden;
    transition: all 300ms ease;
    transform-origin: top;
    width: 100%;
}

/* Collapsed state */
.severity-summary-component.collapsed .severity-summary-content {
    height: 0;
    opacity: 0;
    transform: scaleY(0);
}

.severity-summary-component.collapsed .severity-summary-toggle svg {
    width: 12px !important;
    height: 12px !important;
}

/* Collapsed label */
.severity-summary-collapsed-label {
    position: absolute;
    left: 0;
    top: 0;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 400;
    font-size: 8pt;
    color: rgba(37, 53, 65, 0.9);
    opacity: 0;
    pointer-events: none;
    transition: all 300ms ease;
    cursor: pointer;
}

.severity-summary-collapsed-label:hover {
    color: #F26419;
}

.severity-summary-component.collapsed .severity-summary-collapsed-label {
    opacity: 1;
    pointer-events: auto;
}

.severity-summary-component.collapsed .severity-summary-header {
    opacity: 0;
}

