    background: transparent; /* Ensure no background */
}

/* Header with title and toggle */
.violation-details-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 16px 24px 0 24px;
    margin: 0;
    position: relative;
}

.violation-details-title {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-style: normal;
    font-size: 30px; /* 30pt */
    color: rgba(37, 53, 65, 0.9);
    text-transform: lowercase;
    line-height: 1;
    margin: 0;
}

.violation-details-toggle {
    width: 28px;
    height: 28px;
    background: transparent;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0;
    transition: all 200ms ease;
    position: absolute;
    right: -10px; /* Align with other toggles */
    top: 50%;
    transform: translateY(-50%);
}

.violation-details-toggle svg {
    width: 28px;
    height: 28px;
    stroke: #A8AEB3 !important; /* 40% tint of #253541 */
    fill: none !important;
    transition: stroke 200ms ease;
}

.violation-details-toggle:hover svg {
    stroke: #F26419 !important;
}

/* Override global Lucide styling for violation toggle */
.violation-details-toggle [data-lucide] {
    stroke: inherit !important;
}

/* Collapsible content */
.violation-details-content {
    overflow: hidden;
    transition: all 300ms ease;
    transform-origin: top;
    padding: 8px 14px 0 14px; /* Reduced top padding from 16px to 8px (50%) */
}

.violation-details-component.collapsed .violation-details-content {
    height: 0;
    opacity: 0;
    transform: scaleY(0);
    padding: 0 14px;
}

/* List container */
