    align-items: center;
    justify-content: center;
    position: absolute;
    left: 29px; /* Adjusted to maintain center (26px + 3px) */
    top: 65px; /* Moved up 3px (from 68px to 65px) */
    padding: 6px; /* Increased to 6px padding (3px + 3px) */
    box-sizing: border-box; /* Include padding in width/height */
}

.severity-box.high .severity-badge {
    background-color: #FF0000;
}

.severity-box.medium .severity-badge {
