}

/* Labels and values in same container */
.nav-text-container {
    display: flex;
    align-items: center;
    gap: 4px;
    height: 17px;
    line-height: 17px;
    white-space: nowrap;
}

.nav-label {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 14px;
    color: #9ba1a6;
    text-transform: lowercase;
