}

/* Orange toggle indicator */
.toggle-indicator {
    position: absolute;
    top: -3px; /* Move up 3px */
    width: 90px;
    height: 40px;
    background-color: #f26419;
    border: 3px solid #f8b98c;
    border-radius: 36px;
