}

/* Toggle indicator positions */
.toggle-indicator.tagger {
    left: 0;
}

.toggle-indicator.report {
    left: 90px;
}

.toggle-indicator.evidence {
    left: 180px;
}

/* All legacy navigation styles removed ‚Äî see legacy-removal.html */

