// Severity Boxes toggle functionality (old)
const severityToggle = document.querySelector('.severity-boxes-toggle');
if (severityToggle) {
    severityToggle.addEventListener('click', () => this.toggleSeverityBoxes());
}

// Severity Summary toggle functionality (new)
const severitySummaryToggle = document.querySelector('.severity-summary-toggle');
const severitySummaryContainer = document.querySelector('.severity-summary-component');
const severitySummaryLabel = document.querySelector('.severity-summary-collapsed-label');

if (severitySummaryToggle && severitySummaryContainer) {
    const toggleSeveritySummary = () => {
        const isExpanded = severitySummaryToggle.getAttribute('aria-expanded') === 'true';

        // Toggle collapsed class
        severitySummaryContainer.classList.toggle('collapsed');

        // Update aria-expanded
        severitySummaryToggle.setAttribute('aria-expanded', !isExpanded);

        // Switch icon
        const icon = severitySummaryToggle.querySelector('i');
        if (isExpanded) {
            icon.setAttribute('data-lucide', 'circle-chevron-down');
        } else {
            icon.setAttribute('data-lucide', 'circle-chevron-up');
        }

        // Re-render icons
        lucide.createIcons();

        // Force resize of toggle icon in collapsed state
        setTimeout(() => {
            const toggleSvg = severitySummaryToggle.querySelector('svg');
            if (toggleSvg && severitySummaryContainer.classList.contains('collapsed')) {
                toggleSvg.setAttribute('width', '12');
                toggleSvg.setAttribute('height', '12');
            } else if (toggleSvg) {
                toggleSvg.setAttribute('width', '32');
                toggleSvg.setAttribute('height', '32');
            }
        }, 50);
    };

    severitySummaryToggle.addEventListener('click', toggleSeveritySummary);

    // Add click event to collapsed label
    if (severitySummaryLabel) {
        severitySummaryLabel.addEventListener('click', toggleSeveritySummary);
    }
}

