}

.document-segment {
    position: absolute;
    left: 280px;
    width: 280px;
}

.evidence-page-segment {
    position: absolute;
    left: 640px;
    width: 62px;
}

/* Updated nav-segment base styles */
.nav-segment {
    display: flex;
    align-items: center;
    height: 17px;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    z-index: 1;
}

/* Labels and values in same container */
.nav-text-container {
    display: flex;
