}

.nav-divider:nth-of-type(2) {
    left: 359px;
}

.nav-divider:nth-of-type(3) {
    left: 583px;
}

.report-canvas,
.tagger-canvas,
.evidence-canvas {
    width: 100%;
    height: 100%;
    position: relative;
    background-color: #ffffff;
    overflow: hidden;
    z-index: 1;
}

/* Canvas placeholder for empty states */
.canvas-placeholder {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100%;
    color: #9ba1a6;
    font-family: 'Space Grotesk', sans-serif;
}

.canvas-placeholder h2 {
    font-size: 32px;
    font-weight: 700;
    margin-bottom: 16px;
