530	    handleKeyboard(e) {
531	        // Arrow key navigation for new toggle
532	        if (document.activeElement.classList.contains('view-label')) {
533	            const labels = Array.from(document.querySelectorAll('.view-label'));
534	            const currentIndex = labels.indexOf(document.activeElement);
535	            
536	            if (e.key === 'ArrowLeft' && currentIndex > 0) {
537	                e.preventDefault();
538	                labels[currentIndex - 1].focus();
539	            } else if (e.key === 'ArrowRight' && currentIndex < labels.length - 1) {
540	                e.preventDefault();
541	                labels[currentIndex + 1].focus();
542	            }
543	        }
544	        // Arrow key navigation
545	        if (e.key === 'ArrowLeft' && !e.target.matches('input')) {
546	            e.preventDefault();
547	            if (e.shiftKey) {
548	                this.navigateCreditor(-1);
549	            } else {
550	                this.navigatePage(-1);
551	            }
552	        } else if (e.key === 'ArrowRight' && !e.target.matches('input')) {
553	            e.preventDefault();
554	            if (e.shiftKey) {
555	                this.navigateCreditor(1);
556	            } else {
557	                this.navigatePage(1);
558	            }
559	        }
560	        
561	        // View switching with number keys
562	        if (e.key === '1' && e.ctrlKey) {
563	            e.preventDefault();
564	            this.switchView('report');
565	        } else if (e.key === '2' && e.ctrlKey) {
566	            e.preventDefault();
567	            this.switchView('tagger');
568	        } else if (e.key === '3' && e.ctrlKey) {
569	            e.preventDefault();
570	            this.switchView('evidence');
571	        }
572	    }
