663	    navigateBureau(direction) {
664	        if (!this.navigationData) return;
665	        
666	        const bureauCodes = Object.keys(this.navigationData.bureaus);
667	        const currentIndex = bureauCodes.indexOf(this.currentBureau);
668	        let newIndex = currentIndex + direction;
669	        
670	        // Wrap around
671	        if (newIndex < 0) newIndex = bureauCodes.length - 1;
672	        if (newIndex >= bureauCodes.length) newIndex = 0;
673	        
674	        this.currentBureau = bureauCodes[newIndex];
675	        this.updateBureauDisplay();
676	        this.findAvailableReport();
677	    }
678	    
679	    navigateDate(direction) {
680	        if (!this.navigationData) return;
681	        
682	        // Get all dates for current bureau
683	        const bureauReports = this.navigationData.reports[this.currentBureau];
684	        if (!bureauReports) return;
685	        
686	        const dates = Object.keys(bureauReports).sort();
687	        const currentIndex = dates.indexOf(this.reportDate);
688	        let newIndex = currentIndex + direction;
689	        
690	        // Wrap around
691	        if (newIndex < 0) newIndex = dates.length - 1;
692	        if (newIndex >= dates.length) newIndex = 0;
693	        
694	        this.reportDate = dates[newIndex];
695	        this.updateDateDisplay();
696	        this.findAvailableCreditor();
697	    }
698	    
699	    navigateReportCreditor(direction) {
700	        if (!this.navigationData) return;
701	        
702	        // Get creditors for current bureau and date
703	        const bureauReports = this.navigationData.reports[this.currentBureau];
704	        if (!bureauReports || !bureauReports[this.reportDate]) return;
705	        
706	        const availableCreditors = Object.keys(bureauReports[this.reportDate]);
707	        const currentIndex = availableCreditors.indexOf(this.currentCreditor);
708	        let newIndex = currentIndex + direction;
709	        
