/* ========================================
   Violation Filter Styles
   ======================================== */
.violation-filter {
    margin-bottom: 24px;
    padding: 16px;
    background-color: var(--bg-accent);
    border-radius: 12px;
    border: 1px solid var(--border-subtle);
}

.filter-heading {
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    margin: 0 0 12px 0;
    color: var(--text-secondary);
}

.filter-options {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.filter-option {
    display: flex;
    align-items: center;
    gap: 8px;
    cursor: pointer;
    font-family: 'Space Grotesk', sans-serif;
    font-size: 14px;
    font-weight: 500;
    color: var(--text-primary);
}

.filter-option input[type="radio"] {
    width: 16px;
    height: 16px;
    margin: 0;
    cursor: pointer;
    accent-color: var(--color-primary);
}

.filter-option span {
    text-transform: lowercase;
}

