    height: 17px;
    line-height: 17px;
    white-space: nowrap;
}

.nav-label {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 14px;
    color: #9ba1a6;
    text-transform: lowercase;
    line-height: 1;
    display: inline-flex;
    align-items: center;
}

.nav-value {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 14px;
    color: #ffffff;
    text-transform: lowercase;
    line-height: 1;
    display: inline-flex;
    align-items: center;
}

/* Creditor name specific truncation */
.creditor-segment .nav-value {
    overflow: hidden;
