/* VioVerse Refactor CSS ‚Äì all new styles go here */

/* ========================================
   CSS Custom Properties (Design Tokens)
   ======================================== */
:root {
    /* Primary Colors */
    --color-primary: #0066CC;
    --color-primary-dark: #0052A3;
    --color-primary-light: #3385D6;

    /* Background Colors */
    --bg-dark: #253541;
    --bg-darker: #1A2530;
    --bg-panel: #2D3B47;
    --bg-light: #FFFFFF;

    /* Text Colors */
    --text-primary: #E0E7ED;
    --text-secondary: #94A3B8;
    --text-dark: #1E293B;
    --text-white: #FFFFFF;

    /* Severity Colors (Accessible) */
    --severity-high-bg: #B91C1C;
    --severity-medium-bg: #D97706;
    --severity-low-bg: #059669;

    /* VIObox Colors */
    --viobox-active: #0066CC;
    --viobox-inactive: #E0E7ED;
    --viobox-hover-glow: rgba(0, 102, 204, 0.3);

    /* Border and Shadow */
    --border-subtle: #3E4C59;
    --shadow-sm: 0 2px 4px rgba(0, 0, 0, 0.2);
    --shadow-md: 0 4px 8px rgba(0, 0, 0, 0.3);
    --shadow-focus: 0 0 0 3px #0066CC;

    /* Layout */
    --header-height: 120px;
    --nav-height: 60px;
    --sidebar-width: 380px;
    --content-padding: 24px;

    /* Typography */
    --font-sans: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    --font-mono: 'SF Mono', Monaco, 'Cascadia Code', monospace;

    /* Transitions */
    --transition-fast: 150ms ease;
    --transition-normal: 250ms ease;

    /* Z-index layers */
    --z-base: 1;
    --z-viobox: 10;
    --z-dropdown: 100;
    --z-modal: 1000;
}

/* ========================================
   Reset and Base Styles
   ======================================== */
* {
    box-sizing: border-box;
}

body {
    margin: 0;
    font-family: var(--font-sans);
    background-color: var(--bg-dark);
    color: var(--text-primary);
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

img {
    max-width: 100%;
    height: auto;
    display: block;
}

button {
    font-family: inherit;
    cursor: pointer;
    border: none;
    background: none;
    padding: 0;
    color: inherit;
}

input {
    font-family: inherit;
}

/* ========================================
   Accessibility Utilities
   ======================================== */
.skip-link {
    position: absolute;
    top: -100px;
    left: 20px;
    z-index: var(--z-modal);
    padding: 12px 24px;
    background-color: var(--color-primary);
    color: var(--text-white);
    text-decoration: none;
    border-radius: 4px;
    font-weight: 600;
}

.skip-link:focus {
    top: 20px;
}

/* Focus visible styles for keyboard navigation */
:focus-visible {
    outline: 3px solid var(--color-primary);
    outline-offset: 2px;
    border-radius: inherit;
}

/* ========================================
   Layout Structure
   ======================================== */
.vioverse-app {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
}

/* ========================================
   Docket Header
   ======================================== */
.docket-header {
    background-color: var(--bg-darker);
    border-bottom: 1px solid var(--border-subtle);
    padding: 16px 24px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    transition: margin-top var(--transition-normal);
}

.docket-header[aria-expanded="false"] {
    margin-top: -80px;
}

.docket-content {
    display: flex;
    gap: 32px;
    flex-wrap: wrap;
}

.case-info, .court-info, .judge-info, .deadline-info {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.case-label, .court-label, .judge-label, .deadline-label {
    font-size: 12px;
    color: var(--text-secondary);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.case-value, .court-value, .judge-value, .deadline-value {
    font-size: 14px;
    font-weight: 500;
}

.docket-toggle {
    width: 44px;
    height: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: var(--bg-panel);
    border-radius: 8px;
    transition: background-color var(--transition-fast);
}

.docket-toggle:hover {
    background-color: var(--color-primary-dark);
}

/* ========================================
   Main Navigation
   ======================================== */
.main-nav {
    background-color: var(--bg-panel);
    height: var(--nav-height);
    padding: 0 24px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    border-bottom: 1px solid var(--border-subtle);
}

.nav-left, .nav-center, .nav-right {
    display: flex;
    align-items: center;
    gap: 24px;
}

/* View Toggle Buttons */
.view-toggle {
    display: flex;
    gap: 8px;
    background-color: var(--bg-dark);
    padding: 4px;
    border-radius: 8px;
}

.view-btn {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px 16px;
    border-radius: 6px;
    font-size: 14px;
    font-weight: 500;
    color: var(--text-secondary);
    transition: all var(--transition-fast);
    min-height: 44px;
}

.view-btn i {
    width: 18px;
    height: 18px;
}

.view-btn:hover {
    color: var(--text-primary);
    background-color: rgba(255, 255, 255, 0.05);
}

.view-btn.active {
    background-color: var(--color-primary);
    color: var(--text-white);
}

/* Creditor Pills */
.creditor-pills {
    display: flex;
    gap: 8px;
}

.creditor-pill {
    padding: 6px 16px;
    border-radius: 20px;
    font-size: 14px;
    font-weight: 500;
    background-color: var(--bg-dark);
    color: var(--text-secondary);
    transition: all var(--transition-fast);
    min-height: 36px;
}

.creditor-pill:hover {
    background-color: var(--color-primary-dark);
    color: var(--text-white);
}

.creditor-pill.active {
    background-color: var(--color-primary);
    color: var(--text-white);
}

/* Page Navigation */
.page-nav {
    display: flex;
    align-items: center;
    gap: 12px;
}

.nav-arrow {
    width: 44px;
    height: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--text-secondary);
    background-color: var(--bg-dark);
    border-radius: 8px;
    transition: all var(--transition-fast);
}

.nav-arrow:hover {
    background-color: var(--color-primary);
    color: var(--text-white);
}

.nav-arrow:active {
    background-color: var(--color-primary-dark);
}

.page-input-group {
    display: flex;
    align-items: center;
    gap: 8px;
    font-family: var(--font-mono);
}

.page-input {
    width: 4ch;
    padding: 8px;
    font-size: 16px;
    font-family: var(--font-mono);
    text-align: center;
    background-color: var(--bg-light);
    color: var(--text-dark);
    border: 2px solid transparent;
    border-radius: 6px;
    transition: border-color var(--transition-fast);
}

.page-input:focus {
    border-color: var(--color-primary);
    outline: none;
}

.page-separator, .page-total {
    font-size: 16px;
    color: var(--text-secondary);
}

/* Tips Toggle */
.tips-toggle {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px 16px;
    background-color: var(--text-secondary);
    color: var(--text-white);
    border-radius: 20px;
    font-size: 14px;
    font-weight: 500;
    transition: all var(--transition-fast);
    min-height: 36px;
}

.tips-toggle[aria-pressed="true"] {
    background-color: var(--color-primary);
}

.tips-toggle i {
    width: 16px;
    height: 16px;
}

/* Violation Counter */
.violation-counter {
    display: flex;
    align-items: center;
    gap: 8px;
    font-family: var(--font-mono);
    font-size: 16px;
    font-weight: 600;
}

.violation-counter .count {
    color: var(--color-primary);
}

/* ========================================
   Main Content Area
   ======================================== */
.main-content {
    flex: 1;
    display: flex;
    overflow: hidden;
}

.view-panel {
    width: 100%;
    display: none;
}

.view-panel.active {
    display: block;
}

.content-wrapper {
    display: flex;
    height: 100%;
}

/* ========================================
   Report Canvas
   ======================================== */
.report-canvas-container {
    flex: 1;
    padding: var(--content-padding);
    overflow: auto;
    background-color: var(--bg-dark);
}

.report-canvas {
    position: relative;
    max-width: 1200px;
    margin: 0 auto;
    background-color: var(--bg-panel);
    border-radius: 8px;
    overflow: hidden;
    box-shadow: var(--shadow-md);
}

.report-image {
    width: 100%;
    height: auto;
}

/* VIObox Container */
.viobox-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
}

/* VIObox Styles */
.viobox {
    position: absolute;
    width: 375px;
    height: 24px;
    border: 2px solid var(--viobox-inactive);
    border-radius: 12px;
    background-color: rgba(255, 255, 255, 0.04);
    display: flex;
    align-items: center;
    pointer-events: auto;
    cursor: pointer;
    transition: all var(--transition-fast);
}

.viobox:hover {
    border-color: var(--viobox-active);
    box-shadow: 0 0 8px var(--viobox-hover-glow);
}

.viobox.active {
    border-color: var(--viobox-active);
    background-color: rgba(0, 102, 204, 0.08);
}

.viobox:focus-visible {
    outline: 3px solid var(--text-white);
    outline-offset: -1px;
    box-shadow: 0 0 0 5px var(--viobox-active);
}

/* Severity Icon */
.severity-icon {
    width: 24px;
    height: 24px;
    border-radius: 6px;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: var(--shadow-sm);
}

.severity-icon i {
    width: 16px;
    height: 16px;
    color: var(--text-white);
}

.severity-high {
    background-color: var(--severity-high-bg);
}

.severity-medium {
    background-color: var(--severity-medium-bg);
}

.severity-low {
    background-color: var(--severity-low-bg);
}

/* ========================================
   Violation Sidebar
   ======================================== */
.violation-sidebar, .tagger-sidebar, .evidence-context {
    width: var(--sidebar-width);
    background-color: var(--bg-panel);
    border-left: 1px solid var(--border-subtle);
    padding: var(--content-padding);
    overflow-y: auto;
}

.sidebar-heading {
    font-size: 20px;
    font-weight: 600;
    margin: 0 0 24px 0;
}

/* Violation Statistics */
.violation-stats {
    display: grid;
    gap: 16px;
    margin-bottom: 32px;
}

.stat-card {
    display: flex;
    align-items: center;
    gap: 16px;
    padding: 16px;
    background-color: var(--bg-dark);
    border-radius: 8px;
}

.stat-icon {
    width: 32px;
    height: 32px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.stat-icon i {
    width: 20px;
    height: 20px;
}

.stat-details {
    display: flex;
    flex-direction: column;
}

.stat-label {
    font-size: 12px;
    color: var(--text-secondary);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.stat-value {
    font-size: 24px;
    font-weight: 700;
    color: var(--text-primary);
}

/* Violation List */
.violation-list {
    margin-top: 32px;
}

.list-heading {
    font-size: 16px;
    font-weight: 600;
    margin: 0 0 16px 0;
}

.violations {
    list-style: none;
    padding: 0;
    margin: 0;
}

/* ========================================
   Responsive Design
   ======================================== */
@media (max-width: 1024px) {
    .docket-content {
        gap: 16px;
    }

    .nav-left, .nav-center, .nav-right {
        gap: 16px;
    }

    .violation-sidebar {
        width: 320px;
    }
}

@media (max-width: 768px) {
    .content-wrapper {
        flex-direction: column;
    }

    .violation-sidebar, .tagger-sidebar, .evidence-context {
        width: 100%;
        border-left: none;
        border-top: 1px solid var(--border-subtle);
    }

    .viobox {
        width: 90%;
        max-width: 375px;
    }
}

