    background-color: rgba(255, 255, 255, 0.04);
    display: flex;
    align-items: center;
    pointer-events: auto;
    cursor: pointer;
    transition: all var(--transition-fast);
}

.viobox:hover {
    border-color: var(--viobox-active);
    box-shadow: 0 0 8px var(--viobox-hover-glow);
}

.viobox.active {
    border-color: var(--viobox-active);
    background-color: rgba(0, 102, 204, 0.08);
}

.viobox:focus-visible {
    outline: 3px solid var(--text-white);
    outline-offset: -1px;
    box-shadow: 0 0 0 5px var(--viobox-active);
}

/* Severity Icon */
.severity-icon {
    width: 24px;
    height: 24px;
    border-radius: 6px;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: var(--shadow-sm);
}

.severity-icon i {
    width: 16px;
    height: 16px;
    color: var(--text-white);
}

.severity-high {
    background-color: var(--severity-high-bg);
}

.severity-medium {
    background-color: var(--severity-medium-bg);
}

.severity-low {
    background-color: var(--severity-low-bg);
}

/* ========================================
   Violation Sidebar
   ======================================== */
.violation-sidebar, .tagger-sidebar, .evidence-context {
    width: var(--sidebar-width);
    background-color: var(--bg-panel);
    border-left: 1px solid var(--border-subtle);
    padding: var(--content-padding);
    overflow-y: auto;
}

.sidebar-heading {
    font-size: 20px;
    font-weight: 600;
    margin: 0 0 24px 0;
}

/* Violation Statistics */
.violation-stats {
    display: grid;
    gap: 16px;
    margin-bottom: 32px;
}

.stat-card {
    display: flex;
    align-items: center;
    gap: 16px;
    padding: 16px;
    background-color: var(--bg-dark);
    border-radius: 8px;
}

.stat-icon {
    width: 32px;
    height: 32px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.stat-icon i {
    width: 20px;
    height: 20px;
}

.stat-details {
    display: flex;
    flex-direction: column;
}

.stat-label {
    font-size: 12px;
    color: var(--text-secondary);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.stat-value {
    font-size: 24px;
    font-weight: 700;
    color: var(--text-primary);
}

/* Violation List */
.violation-list {
    margin-top: 32px;
}

.list-heading {
    font-size: 16px;
    font-weight: 600;
    margin: 0 0 16px 0;
}

.violations {
    list-style: none;
    padding: 0;
    margin: 0;
}

/* ========================================
   Responsive Design
   ======================================== */
@media (max-width: 1024px) {
    .docket-content {
        gap: 16px;
    }

    .nav-left, .nav-center, .nav-right {
        gap: 16px;
    }

    .violation-sidebar {
        width: 320px;
    }
}

@media (max-width: 768px) {
    .content-wrapper {
        flex-direction: column;
    }

    .violation-sidebar, .tagger-sidebar, .evidence-context {
        width: 100%;
        border-left: none;
        border-top: 1px solid var(--border-subtle);
    }

    .viobox {
        width: 90%;
        max-width: 375px;
    }
}

