toolUseResult.originalFileContents:
/* VioVerse Refactor CSS ‚Äì all new styles go here */

/* ========================================
   CSS Custom Properties (Design Tokens)
   ======================================== */
:root {
    /* Primary Colors */
    --color-primary: #0066CC;
    --color-primary-dark: #0052A3;
    --color-primary-light: #3385D6;

    /* Background Colors */
    --bg-dark: #253541;
    --bg-darker: #1A2530;
    --bg-panel: #2D3B47;
    --bg-light: #FFFFFF;

    /* Text Colors */
    --text-primary: #E0E7ED;
    --text-secondary: #94A3B8;
    --text-dark: #1E293B;
    --text-white: #FFFFFF;

    /* Severity Colors (Accessible) */
    --severity-high-bg: #B91C1C;
    --severity-medium-bg: #D97706;
    --severity-low-bg: #059669;

    /* VIObox Colors */
    --viobox-active: #0066CC;
    --viobox-inactive: #E0E7ED;
    --viobox-hover-glow: rgba(0, 102, 204, 0.3);

    /* Border and Shadow */
    --border-subtle: #3E4C59;
    --shadow-sm: 0 2px 4px rgba(0, 0, 0, 0.2);
    --shadow-md: 0 4px 8px rgba(0, 0, 0, 0.3);
    --shadow-focus: 0 0 0 3px #0066CC;

    /* Layout */
    --header-height: 120px;
    --nav-height: 60px;
    --sidebar-width: 380px;
    --content-padding: 24px;

    /* Typography */
    --font-sans: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    --font-mono: 'SF Mono', Monaco, 'Cascadia Code', monospace;

    /* Transitions */
    --transition-fast: 150ms ease;
    --transition-normal: 250ms ease;

    /* Z-index layers */
    --z-base: 1;
    --z-viobox: 10;
    --z-dropdown: 100;
    --z-modal: 1000;
}

/* ========================================
   Reset and Base Styles
   ======================================== */
* {
    box-sizing: border-box;
}

body {
    margin: 0;
    font-family: var(--font-sans);
    background-color: var(--bg-dark);
    color: var(--text-primary);
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

img {
    max-width: 100%;
    height: auto;
    display: block;
}

button {
    font-family: inherit;
    cursor: pointer;
    border: none;
    background: none;
    padding: 0;
    color: inherit;
}

input {
    font-family: inherit;
}

/* ========================================
   Accessibility Utilities
   ======================================== */
.skip-link {
    position: absolute;
    top: -100px;
    left: 20px;
    z-index: var(--z-modal);
    padding: 12px 24px;
    background-color: var(--color-primary);
    color: var(--text-white);
    text-decoration: none;
    border-radius: 4px;
    font-weight: 600;
}

.skip-link:focus {
    top: 20px;
}

/* Focus visible styles for keyboard navigation */
:focus-visible {
    outline: 3px solid var(--color-primary);
    outline-offset: 2px;
    border-radius: inherit;
}

/* ========================================
   Layout Structure
   ======================================== */
.vioverse-app {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
}

/* ========================================
   Docket Header
   ======================================== */
.docket-header {
    background-color: var(--bg-darker);
    border-bottom: 1px solid var(--border-subtle);
    padding: 16px 24px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    transition: margin-top var(--transition-normal);
}

.docket-header[aria-expanded="false"] {
    margin-top: -80px;
}

.docket-content {
    display: flex;
    gap: 32px;
    flex-wrap: wrap;
}

.case-info, .court-info, .judge-info, .deadline-info {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.case-label, .court-label, .judge-label, .deadline-label {
    font-size: 12px;
    color: var(--text-secondary);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.case-value, .court-value, .judge-value, .deadline-value {
    font-size: 14px;
    font-weight: 500;
}

.docket-toggle {
    width: 44px;
    height: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: var(--bg-panel);
    border-radius: 8px;
    transition: background-color var(--transition-fast);
}

.docket-toggle:hover {
    background-color: var(--color-primary-dark);
}

/* ========================================
   Main Navigation
   ======================================== */
.main-nav {
    background-color: var(--bg-panel);
    height: var(--nav-height);
    padding: 0 24px;
    display: flex;
    align-items: center;
