// VioVerse Refactor JS ‚Äì initialize component behavior here

/**
 * VioVerse Main Application
 * Modular JavaScript for FCRA violation analysis platform
 */

class VioVerse {
    constructor() {
        this.currentView = 'report';
        this.currentPage = 57;
        this.totalPages = 999;
        this.tipsEnabled = false;
        this.docketExpanded = true;
        this.selectedViolations = new Set();

        // Sample violation data for demo
        this.violations = [
            { id: 1, x: 100, y: 200, severity: 'high', text: 'Missing account status' },
            { id: 2, x: 100, y: 300, severity: 'medium', text: 'Incorrect balance' },
            { id: 3, x: 100, y: 400, severity: 'low', text: 'Date discrepancy' }
        ];

        this.init();
    }

    init() {
        // Wait for DOM to be ready
        if (document.readyState === 'loading') {
            document.addEventListener('DOMContentLoaded', () => this.setupEventListeners());
        } else {
            this.setupEventListeners();
        }
    }

    setupEventListeners() {
        // View toggle buttons
        const viewButtons = document.querySelectorAll('.view-btn');
        viewButtons.forEach(btn => {
            btn.addEventListener('click', (e) => this.switchView(e.currentTarget.dataset.view));
        });

        // Docket toggle
        const docketToggle = document.querySelector('.docket-toggle');
        if (docketToggle) {
            docketToggle.addEventListener('click', () => this.toggleDocket());
        }

        // Tips toggle
        const tipsToggle = document.querySelector('.tips-toggle');
        if (tipsToggle) {
            tipsToggle.addEventListener('click', () => this.toggleTips());
        }

        // Page navigation
        const prevBtn = document.querySelector('.nav-arrow.prev');
        const nextBtn = document.querySelector('.nav-arrow.next');
        const pageInput = document.querySelector('.page-input');

        if (prevBtn) prevBtn.addEventListener('click', () => this.navigatePage(-1));
        if (nextBtn) nextBtn.addEventListener('click', () => this.navigatePage(1));
        if (pageInput) {
            pageInput.addEventListener('change', (e) => this.goToPage(parseInt(e.target.value)));
            pageInput.addEventListener('keypress', (e) => {
                if (e.key === 'Enter') {
                    e.target.blur();
                }
            });
        }

        // Creditor pills
        const creditorPills = document.querySelectorAll('.creditor-pill');
        creditorPills.forEach(pill => {
            pill.addEventListener('click', (e) => this.selectCreditor(e.currentTarget));
        });

        // Initialize VIOboxes
        this.renderViolations();

        // Initialize counter
        this.updateViolationCounter();

        // Keyboard navigation
        document.addEventListener('keydown', (e) => this.handleKeyboard(e));
    }

    switchView(viewName) {
        if (viewName === this.currentView) return;

        // Update view state
        this.currentView = viewName;
        document.querySelector('.vioverse-app').dataset.view = viewName;

        // Update button states
        document.querySelectorAll('.view-btn').forEach(btn => {
            const isActive = btn.dataset.view === viewName;
            btn.classList.toggle('active', isActive);
            btn.setAttribute('aria-selected', isActive);
        });

        // Update panel visibility
        document.querySelectorAll('.view-panel').forEach(panel => {
            const isActive = panel.id === `${viewName}-view`;
            panel.classList.toggle('active', isActive);
            panel.hidden = !isActive;
        });

        // Announce view change to screen readers
        this.announce(`Switched to ${viewName} view`);
    }

    toggleDocket() {
        this.docketExpanded = !this.docketExpanded;
        const header = document.querySelector('.docket-header');
        const toggle = document.querySelector('.docket-toggle');

        if (header && toggle) {
            if (this.docketExpanded) {
                header.removeAttribute('aria-expanded');
                header.style.marginTop = '0';
            } else {
                header.setAttribute('aria-expanded', 'false');
                header.style.marginTop = '-80px';
            }

            // Update icon
            const icon = toggle.querySelector('i');
            if (icon) {
                icon.setAttribute('data-lucide', this.docketExpanded ? 'chevron-up' : 'chevron-down');
                lucide.createIcons();
            }
        }
    }

    toggleTips() {
        this.tipsEnabled = !this.tipsEnabled;
        const toggle = document.querySelector('.tips-toggle');

        if (toggle) {
            toggle.setAttribute('aria-pressed', this.tipsEnabled);
            document.body.classList.toggle('tooltip-mode', this.tipsEnabled);

            // Update button text
            const span = toggle.querySelector('span');
            if (span) {
                span.textContent = this.tipsEnabled ? 'Tips ON' : 'Tips OFF';
            }

            // Update icon style
            const icon = toggle.querySelector('i');
            if (icon) {
                icon.style.fill = this.tipsEnabled ? 'currentColor' : 'none';
            }
        }

        this.announce(`Tooltips ${this.tipsEnabled ? 'enabled' : 'disabled'}`);
    }

    navigatePage(direction) {
        const newPage = this.currentPage + direction;
        if (newPage >= 1 && newPage <= this.totalPages) {
            this.goToPage(newPage);
        }
    }

    goToPage(pageNum) {
        if (pageNum < 1 || pageNum > this.totalPages || isNaN(pageNum)) {
            return;
        }

        this.currentPage = pageNum;
        const pageInput = document.querySelector('.page-input');
        if (pageInput) {
            pageInput.value = pageNum;
        }

        // Update the report image source
        const reportImage = document.querySelector('.report-image');
        if (reportImage) {
            // This would be dynamically generated based on current creditor/bureau
            // For now, just announce the change
            console.log(`Loading page ${pageNum}`);
        }

        // Clear selections when changing pages
        this.selectedViolations.clear();
        document.querySelectorAll('.viobox').forEach(box => box.classList.remove('active'));

        this.announce(`Page ${pageNum} of ${this.totalPages}`);

        // Update violation counter
        this.updateViolationCounter();
    }

    selectCreditor(pill) {
        // Remove active state from all pills
        document.querySelectorAll('.creditor-pill').forEach(p => {
            p.classList.remove('active');
            p.setAttribute('aria-pressed', 'false');
        });

        // Add active state to clicked pill
        pill.classList.add('active');
        pill.setAttribute('aria-pressed', 'true');

        this.announce(`Selected ${pill.textContent} creditor`);
    }

    renderViolations() {
        const container = document.querySelector('.viobox-container');
        if (!container) return;

        // Clear existing boxes
        container.innerHTML = '';

        // Render each violation
        this.violations.forEach(violation => {
            const viobox = this.createVIObox(violation);
            container.appendChild(viobox);
        });

        // Update sidebar
        this.updateViolationSidebar();
    }

    createVIObox(violation) {
        const box = document.createElement('button');
        box.className = 'viobox';
        box.setAttribute('role', 'button');
        box.setAttribute('aria-label', `${violation.severity} severity violation: ${violation.text}`);
        box.setAttribute('tabindex', '0');
        box.setAttribute('data-violation-id', violation.id);
        box.style.left = `${violation.x}px`;
        box.style.top = `${violation.y}px`;

        // Add severity icon
        const icon = document.createElement('div');
        icon.className = `severity-icon severity-${violation.severity}`;

        const iconEl = document.createElement('i');
        const iconMap = {
            high: 'shield-x',
            medium: 'alert-triangle',
            low: 'info'
        };
        iconEl.setAttribute('data-lucide', iconMap[violation.severity]);
        icon.appendChild(iconEl);

        box.appendChild(icon);

        // Add tooltip for violation text
        if (this.tipsEnabled) {
            const tooltip = document.createElement('span');
            tooltip.className = 'viobox-tooltip';
            tooltip.textContent = violation.text;
            box.appendChild(tooltip);
        }

        // Add click handler
        box.addEventListener('click', (e) => {
            e.preventDefault();
            this.selectViolation(violation, e.currentTarget);
        });

        return box;
    }

    selectViolation(violation, viobox) {
        const isSelected = this.selectedViolations.has(violation.id);

        if (isSelected) {
            // Deselect
            this.selectedViolations.delete(violation.id);
            viobox.classList.remove('active');
            this.announce(`Deselected ${violation.severity} severity violation`);
        } else {
            // Select
            this.selectedViolations.add(violation.id);
            viobox.classList.add('active');
            this.announce(`Selected ${violation.severity} severity violation: ${violation.text}`);
        }

        // Update counter
        this.updateViolationCounter();
    }

    updateViolationSidebar() {
        // Update statistics to show selected violations
        const selectedViolations = this.violations.filter(v => this.selectedViolations.has(v.id));
        const stats = {
            high: selectedViolations.filter(v => v.severity === 'high').length,
            medium: selectedViolations.filter(v => v.severity === 'medium').length,
            low: selectedViolations.filter(v => v.severity === 'low').length
        };

        document.querySelectorAll('.stat-value').forEach((el, index) => {
            const severity = ['high', 'medium', 'low'][index];
            if (severity && stats[severity] !== undefined) {
                el.textContent = stats[severity];
            }
        });

        // Update violation list
        const listContainer = document.querySelector('.violations');
        if (listContainer) {
            listContainer.innerHTML = '';

            this.violations.forEach(violation => {
                const li = document.createElement('li');
                li.className = 'violation-item';
                li.innerHTML = `
                    <div class="violation-item-icon severity-icon severity-${violation.severity}">
                        <i data-lucide="${violation.severity === 'high' ? 'shield-x' : 
                                          violation.severity === 'medium' ? 'alert-triangle' : 'info'}"></i>
                    </div>
                    <div class="violation-item-text">
                        <span class="violation-severity">${violation.severity.charAt(0).toUpperCase() + violation.severity.slice(1)}</span>
                        <span class="violation-description">${violation.text}</span>
                    </div>
                `;
                listContainer.appendChild(li);
            });

            // Re-initialize Lucide icons and restore selected state
            lucide.createIcons();

            // Restore selected state for VIOboxes
            this.selectedViolations.forEach(id => {
                const box = document.querySelector(`[data-violation-id="${id}"]`);
                if (box) box.classList.add('active');
            });
        }
    }

    updateViolationCounter() {
        const counter = document.querySelector('.violation-counter .count');
        if (counter) {
            // Show number of selected violations
            counter.textContent = this.selectedViolations.size;
        }
    }

    handleKeyboard(e) {
        // Arrow key navigation
        if (e.key === 'ArrowLeft' && !e.target.matches('input')) {
            e.preventDefault();
            this.navigatePage(-1);
        } else if (e.key === 'ArrowRight' && !e.target.matches('input')) {
            e.preventDefault();
            this.navigatePage(1);
        }

        // View switching with number keys
        if (e.key === '1' && e.ctrlKey) {
            e.preventDefault();
            this.switchView('report');
        } else if (e.key === '2' && e.ctrlKey) {
            e.preventDefault();
            this.switchView('tagger');
        } else if (e.key === '3' && e.ctrlKey) {
            e.preventDefault();
            this.switchView('evidence');
        }
    }

    announce(message) {
        // Create or update live region for screen reader announcements
        let liveRegion = document.getElementById('vioverse-live-region');
        if (!liveRegion) {
            liveRegion = document.createElement('div');
            liveRegion.id = 'vioverse-live-region';
            liveRegion.setAttribute('aria-live', 'polite');
            liveRegion.setAttribute('aria-atomic', 'true');
            liveRegion.style.position = 'absolute';
            liveRegion.style.left = '-10000px';
            liveRegion.style.width = '1px';
            liveRegion.style.height = '1px';
            liveRegion.style.overflow = 'hidden';
            document.body.appendChild(liveRegion);
        }

        liveRegion.textContent = message;
    }
}

