.creditor-segment {
    position: absolute;
    left: 375px;
    width: 150px;
}

.creditor-segment .nav-text-container {
    max-width: 150px;
    overflow: hidden;
}

/* Page segment */
.page-segment {
    position: absolute;
    left: 600px;
    width: 62px;
}

/* Evidence Navigation Segments */
.category-segment {
    position: absolute;
    left: 20px;
    width: 180px;
}

.document-segment {
    position: absolute;
    left: 280px;
    width: 280px;
}

.evidence-page-segment {
    position: absolute;
    left: 640px;
    width: 62px;
}

/* Updated nav-segment base styles */
.nav-segment {
    display: flex;
    align-items: center;
