    opacity: 0;
    transform: scaleY(0);
    padding: 0 14px;
}

/* List container */
.violation-details-list {
    display: flex;
    flex-direction: column;
    gap: 8px; /* Space between violation boxes - reduced by 40% */
}

/* Individual violation box */
.violation-box {
