    position: absolute;
    top: 6px;
    width: 110px;
    height: 34px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 9.6pt;
    color: rgba(37, 53, 65, 0.9);
    text-align: center;
    line-height: 1.2;
}

/* Dot and counter group */
.dot-counter-group {
    position: absolute;
    bottom: 11px;
    display: flex;
    align-items: center;
    gap: 2px;
    left: 50%;
    transform: translateX(-50%);
}

/* Badge dot */
.badge-dot {
    width: 38px;
    height: 38px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.severity-high .badge-dot {
    background-color: #FF0000;
}

.severity-medium .badge-dot {
    background-color: #F26419;
}

.severity-low .badge-dot {
    background-color: #F2B919;
}

.badge-dot svg {
    color: white !important;
    stroke: white !important;
    fill: none !important;
    width: 28px !important;
    height: 28px !important;
    display: block;
    margin: 0 auto;
}

/* Icon-specific adjustments */
.severity-high .badge-dot svg {
    position: relative;
    top: 0px;
    width: 33.6px !important;
    height: 33.6px !important;
}

.severity-medium .badge-dot svg {
    position: relative;
    top: -2.3px;
    width: 30.8px !important;
    height: 30.8px !important;
}

.severity-low .badge-dot svg {
    width: 33.6px !important;
    height: 33.6px !important;
}

/* Override global Lucide styling for severity badges */
.severity-badge [data-lucide] {
    stroke: #ffffff !important;
}

/* Severity counter */
.severity-counter {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 33.5pt;
    color: rgba(37, 53, 65, 0.9);
    display: flex;
    align-items: center;
    justify-content: center;
    line-height: 1;
    min-width: 36px;
    position: relative;
    top: 2px;
}

