    font-size: 16px;
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    min-height: 100vh;
}

img {
    max-width: 100%;
    height: auto;
    display: block;
}

button {
    font-family: inherit;
    cursor: pointer;
    border: none;
    background: none;
    padding: 0;
    color: inherit;
}

input {
    font-family: inherit;
}

/* ========================================
   Accessibility Utilities
   ======================================== */
.skip-link {
    position: absolute;
    top: -100px;
    left: 20px;
