    outline-offset: 2px;
    border-radius: inherit;
}

/* ========================================
   Layout Structure
   ======================================== */
.vioverse-app {
    position: relative;
    min-width: 1920px;
    min-height: 100vh;
    margin: 0 auto;
    background-color: #4f5a63;
    overflow: visible;
    z-index: 0;
