    align-items: center;
    justify-content: center;
    line-height: 1;
    min-width: 36px;
    position: relative;
    top: 2px;
}

/* Tighter letter spacing for 3-digit numbers */
.severity-counter.three-digits {
    letter-spacing: -0.05em;
}

