}

/* Toggle indicator positions */
.toggle-indicator.tagger {
    left: 0;
}

.toggle-indicator.report {
    left: 90px;
}

.toggle-indicator.evidence {
    left: 180px;
}

/* All legacy navigation styles removed â€” see legacy-removal.html */

/* ========================================
   Main Content Area
   ======================================== */
.main-content {
    position: relative;
    background: transparent;
    width: 100%;
    min-height: calc(100vh - var(--header-height) - var(--nav-height));
    padding: 40px 0;
}

.view-panel {
    width: 100%;
    display: none;
}

.view-panel.active {
    display: block;
}

.content-wrapper {
    position: relative;
    width: 100%;
    height: 100%;
}

/* ========================================
   Report Canvas
   ======================================== */
.report-canvas-container,
.tagger-canvas-container,
.evidence-canvas-container {
    position: fixed;
