    font-weight: 700;
    font-size: 10px;
    text-transform: lowercase;
    padding: 6px 12px;
    border-radius: 18px;
    white-space: nowrap;
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.2s ease, visibility 0.2s ease;
    transition-delay: 0.15s;
    pointer-events: none;
    z-index: 10000;
}

.nav-tooltip::after {
