    border-radius: 12px;
    border: 1px solid var(--border-subtle);
}

/* ========================================
   Responsive Design
   ======================================== */
@media (max-width: 1024px) {
    .docket-content {
        gap: 16px;
    }

    .nav-left, .nav-center, .nav-right {
        gap: 16px;
    }

    .violation-sidebar {
        width: 320px;
    }
}

@media (max-width: 768px) {
    .content-wrapper {
        flex-direction: column;
    }

    .violation-sidebar, .tagger-sidebar, .evidence-sidebar {
        width: 100%;
        border-left: none;
        border-top: 1px solid var(--border-subtle);
        margin-top: 16px;
        height: auto;
    }

    .viobox {
        width: 90%;
        max-width: 557px;
    }
}

/* ========================================
   Tooltip System
   ======================================== */
.tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
    transform: translateX(-50%);
    padding: 8px 12px;
    background-color: #f26419;
    color: var(--text-white);
    border-radius: 8px;
    font-family: 'Space Grotesk', sans-serif;
    font-size: 16px;
    font-weight: 700;
    text-transform: lowercase;
    white-space: nowrap;
    pointer-events: none;
    opacity: 0;
    transition: opacity var(--transition-fast);
    z-index: 10000;
    box-shadow: var(--shadow-lg);
}

.tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: #f26419;
}

.tooltip-mode .tips-toggle:hover .tooltip {
    opacity: 1;
}

/* VIObox Tooltips */
.viobox-tooltip {
    position: absolute;
    bottom: calc(100% + 8px);
    left: 50%;
    transform: translateX(-50%);
    padding: 8px 16px;
    background-color: #f26419;
    color: var(--text-white);
    border-radius: 8px;
    font-size: 14px;
    font-weight: var(--font-regular);
    white-space: nowrap;
    pointer-events: none;
    opacity: 0;
    transition: opacity var(--transition-fast);
    z-index: 10000;
    box-shadow: var(--shadow-lg);
}

.viobox-tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: #f26419;
}

.tooltip-mode .viobox:hover .viobox-tooltip {
    opacity: 1;
}

/* ========================================
   Enhanced Sidebar Top Stack Components
   ======================================== */

/* Main wrapper for the stacked components */
.sidebar-top-stack {
    display: flex;
    flex-direction: column;
    gap: 0; /* No gap between components */
    width: 100%;
    position: relative;
    z-index: 1; /* Keep below heading */
}

/* ========================================
   Filter Counter Component
   ======================================== */
.filter-counter {
    display: flex;
    flex-direction: column;
    width: 100%;
}

/* Header container - always visible */
.filter-counter-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 16px 24px 0 24px;
    margin: 0; /* No negative margin needed */
    position: relative;
    z-index: 2; /* Ensure header is above background but below high-priority elements */
}

.filter-counter-title {
    font-family: "jaf-bernino-sans-comp", sans-serif;
    font-weight: 800;
    font-size: 24pt;
    color: #253541;
    text-transform: lowercase;
    line-height: 1;
    margin: 0;
}

.filter-counter-toggle {
    width: 28px;
    height: 28px;
    background: transparent;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    transition: all 200ms ease;
    padding: 0;
    position: relative;
    z-index: 10; /* Ensure button is above other elements */
}

/* Hide collapsed button by default */
.filter-counter-toggle-collapsed {
    display: none;
}

/* Show/hide buttons based on state */
.filter-counter.collapsed .filter-counter-toggle-expanded {
    display: none;
}

.filter-counter.collapsed .filter-counter-toggle-collapsed {
    display: flex;
}

.filter-counter-toggle svg {
    width: 28px;
    height: 28px;
    stroke: #A8AEB3 !important; /* 40% tint of #253541 - matching other toggles */
    fill: none !important;
    transition: stroke 200ms ease;
}

/* More specific selector to ensure visibility */
.filter-counter-toggle > svg,
.filter-counter-toggle svg[data-lucide] {
    stroke: #A8AEB3 !important; /* 40% tint for better visibility */
    fill: none !important;
    opacity: 1 !important;
    visibility: visible !important;
}

.filter-counter-toggle:hover svg,
.filter-counter-toggle:hover [data-lucide] {
    stroke: #F26419 !important;
    stroke-opacity: 1 !important; /* 100% opacity on hover */
}

/* Ensure hover works in collapsed state too */
.filter-counter.collapsed .filter-counter-toggle:hover svg,
.filter-counter.collapsed .filter-counter-toggle:hover [data-lucide] {
    stroke: #F26419 !important;
}

/* Filters label - Only visible when collapsed */
.filter-id-label {
    position: fixed;
    left: 1516px;
    top: 140px;
    width: auto; /* Auto width for longer text */
    height: 16px;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 400; /* regular */
    font-size: 8pt;
    color: #253541;
    text-align: left;
    line-height: 1;
    display: none; /* Hidden by default */
    z-index: 11; /* Above toggle button */
    padding: 0; /* No padding */
    margin: 0; /* No margin */
    white-space: nowrap; /* Prevent text wrapping */
    cursor: pointer; /* Make it clickable */
    transition: color 200ms ease; /* Smooth color transition */
}

.filter-id-label:hover {
    color: #F26419; /* Orange on hover */
}

/* Collapsible content container */
.filter-counter-content {
    overflow: hidden;
    transition: all 300ms ease;
    transform-origin: top;
}

.filter-counter.collapsed .filter-counter-content {
    height: 0;
    opacity: 0;
    transform: scaleY(0);
}

/* When collapsed, hide title and show filters label */
.filter-counter.collapsed .filter-counter-title {
    display: none;
}

.filter-counter.collapsed .filter-id-label {
    display: block !important;
}

/* Collapsed state - reposition the toggle button */
.filter-counter.collapsed .filter-counter-toggle {
    position: fixed !important;
    left: 1571px !important; /* 1576px - 5px (move left) = 1571px */
    top: 132px !important; /* 131px + 1px (move down) = 132px */
}

/* Collapsed state chevron color and size */
.filter-counter.collapsed .filter-counter-toggle svg,
.filter-counter.collapsed .filter-counter-toggle svg[data-lucide="circle-chevron-down"] {
    stroke: #9ba1a6 !important; /* 30% tint of #253541 */
    width: 10.5px !important; /* Increased by 50%: 7px * 1.5 = 10.5px */
    height: 10.5px !important; /* Increased by 50%: 7px * 1.5 = 10.5px */
}

/* Ensure collapsed chevron icon has correct color and size */
.filter-counter.collapsed .filter-counter-toggle [data-lucide="circle-chevron-down"] {
    stroke: #9ba1a6 !important; /* 30% tint of #253541 */
    width: 10.5px !important; /* Increased by 50% */
    height: 10.5px !important; /* Increased by 50% */
}

/* Intro text */
.filter-counter-intro {
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 700;
    font-size: 8.5pt;
    color: rgba(37, 53, 65, 0.9);
