    background-color: #2a2a2a;
    color: var(--text-primary);
    font-size: 16px;
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    min-height: 100vh;
}

img {
    max-width: 100%;
    height: auto;
    display: block;
}

button {
    font-family: inherit;
    cursor: pointer;
    border: none;
    background: none;
    padding: 0;
    color: inherit;
}

input {
    font-family: inherit;
}

/* ========================================
   Accessibility Utilities
   ======================================== */
.skip-link {
    position: absolute;
    top: -100px;
    left: 20px;
    z-index: var(--z-modal);
    padding: 12px 24px;
    background-color: var(--color-primary);
    color: var(--text-white);
    text-decoration: none;
    border-radius: 4px;
    font-weight: 600;
}

.skip-link:focus {
    top: 20px;
}

/* Focus visible styles for keyboard navigation */
:focus-visible {
    outline: 3px solid var(--color-primary);
    outline-offset: 2px;
    border-radius: inherit;
}

/* ========================================
   Layout Structure
   ======================================== */
.vioverse-app {
    position: relative;
    min-width: 1920px;
    min-height: 100vh;
    margin: 0 auto;
    background-color: #4f5a63;
    overflow: visible;
    z-index: 0;
}

/* ========================================
