    cursor: pointer;
    position: relative;
    padding: 2px 0; /* Add 2px vertical padding between items */
}

.filter-option input[type="radio"] {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    width: 14px;
    height: 14px;
    border: 2.5px solid #253541; /* 2.5pt stroke (decreased by 0.5pt) */
